mapper.select:
SELECT G_CATE_CODE,CATE_NAME
FROM G_CATE
WHERE G_CATE_BCODE = #{g_cate_bcode} AND G_CATE_MCODE = #{g_cate_code}

SELECT COUNT(*)+1 AS COUNT
FROM G_APPLY
WHERE G_POST_CODE =#{g_post_code}

SELECT COUNT(*)
FROM VIEW_CATEMAINLIST
WHERE G_CATE_BCODE = #{g_cate_bcode}

SELECT COUNT(*)
FROM  VIEW_CATEMAINLIST
WHERE G_CATE_BCODE = #{g_cate_bcode} AND G_CATE_MCODE = #{g_cate_code}
