mapper.select:
SELECT R_CATE_CODE, CATE_NAME
FROM R_CATE
WHERE R_CATE_BCODE = #{r_cate_bcode}  AND R_CATE_MCODE = #{r_cate_code}

SELECT *
FROM VIEW_RCATEMAINLIST
WHERE R_CATE_BCODE = #{r_cate_bcode} AND R_CATE_MCODE = #{r_cate_code}

SELECT BOOKING_END_DATE
FROM R_POST
WHERE R_POST_CODE = #{r_post_code}
